INSERT INTO local_news (id, prefecture, municipality, name, summary, body, source_url, category, created_at) VALUES
  ('1', '東京都', '新宿区', 'デジタル庁が新宿区でDX推進セミナー開催', 'デジタル庁が新宿区で地域DX推進セミナーを開催。自治体職員向けの研修プログラムを実施。', 'デジタル庁は新宿区の区役所で地域DX推進セミナーを開催しました。このセミナーでは、自治体職員を対象にデジタル化の基礎知識から実践的なノウハウまで幅広く学べる内容となっています。参加者からは「具体的な事例が多く、参考になった」との声が寄せられています。', 'https://www.digital.go.jp/news/2024/01/15/seminar-shinjuku', '行政DX', NOW()),
  ('2', '大阪府', '大阪市', '大阪市がスマートシティ構想を発表', '大阪市がスマートシティ構想の詳細を発表。IoT技術を活用した都市インフラの整備を計画。', '大阪市は2024年度から本格始動するスマートシティ構想の詳細を発表しました。IoTセンサーを活用した交通管理システムや環境モニタリング、高齢者見守りサービスなど、多様なデジタルサービスを展開予定です。', 'https://www.city.osaka.lg.jp/news/2024/01/20/smart-city', '行政DX', NOW() - INTERVAL '1 hour'),
  ('3', '福岡県', '福岡市', '高齢者向けスマホ教室が好評', '福岡市の公民館で開催されたスマホ教室が大盛況。参加者の9割が「生活が便利になった」と回答。', '福岡市の公民館で開催されている高齢者向けスマホ教室が好評を博しています。LINEの使い方からQRコード決済まで、日常生活に必要な機能を丁寧に指導。参加者からは「孫との連絡が取りやすくなった」「買い物が便利になった」などの声が寄せられています。', 'https://www.city.fukuoka.lg.jp/news/2024/01/18/smartphone-class', '教育・学習', NOW() - INTERVAL '2 hours'),
  ('4', '京都府', '京都市', '地域アプリで買い物支援サービス開始', '京都市の山間部で買い物支援アプリが本格稼働。高齢者の買い物難民問題の解決に貢献。', '京都市の山間部地域で買い物支援アプリ「まちなかマーケット」が本格稼働を開始しました。このアプリでは、地域の商店と住民をマッチングし、買い物代行や配達サービスを提供。高齢者の買い物難民問題の解決に大きく貢献しています。', 'https://www.city.kyoto.lg.jp/news/2024/01/16/shopping-app', '産業・ビジネス', NOW() - INTERVAL '3 hours'),
  ('5', '北海道', '札幌市', '防災アプリで避難訓練を実施', '札幌市が防災アプリを活用した避難訓練を実施。市民の防災意識向上に効果。', '札幌市は防災アプリ「さっぽろ防災ナビ」を活用した大規模避難訓練を実施しました。アプリのプッシュ通知機能で避難情報を配信し、市民の迅速な避難行動を支援。訓練参加者からは「アプリがあることで安心感が増した」との声が聞かれました。', 'https://www.city.sapporo.jp/news/2024/01/14/disaster-drill', '防災・安全', NOW() - INTERVAL '4 hours'),
  ('6', '東京都', '渋谷区', '子育て支援アプリがリニューアル', '渋谷区の子育て支援アプリが大幅リニューアル。保育園の空き状況確認機能を追加。', '渋谷区の子育て支援アプリ「しぶや子育てナビ」が大幅リニューアルしました。新機能として、リアルタイムでの保育園空き状況確認や、子育てイベントの予約機能を追加。利用者からは「とても便利になった」と好評です。', 'https://www.city.shibuya.tokyo.jp/news/2024/01/12/childcare-app', '福祉・子育て', NOW() - INTERVAL '5 hours'),
  ('7', '大阪府', '堺市', '環境モニタリングシステム導入', '堺市が環境モニタリングシステムを導入。大気質や騒音レベルをリアルタイム監視。', '堺市は市内各所に環境モニタリングセンサーを設置し、大気質や騒音レベルをリアルタイムで監視するシステムを導入しました。データは市民向けアプリでも確認でき、環境意識の向上に寄与しています。', 'https://www.city.sakai.lg.jp/news/2024/01/10/environment-monitoring', '環境・暮らし', NOW() - INTERVAL '6 hours'),
  ('8', '福岡県', '北九州市', 'デジタルイベント開催', '北九州市でデジタル技術体験イベントを開催。VRやAI技術を市民が体験。', '北九州市のイベントホールで「デジタルテクノロジー体験フェア」が開催されました。VRゴーグルやAIチャットボットなど、最新のデジタル技術を市民が実際に体験できるイベントで、多くの家族連れで賑わいました。', 'https://www.city.kitakyushu.lg.jp/news/2024/01/08/digital-event', 'イベント', NOW() - INTERVAL '7 hours'),
  ('9', '京都府', '宇治市', '観光アプリで地域活性化', '宇治市の観光アプリが地域活性化に貢献。観光客数が前年比20%増加。', '宇治市が開発した観光アプリ「うじ観光ナビ」が地域活性化に大きく貢献しています。アプリのAR機能で名所の歴史を学べる機能が人気で、観光客数が前年比20%増加。地元商店街の売上向上にもつながっています。', 'https://www.city.uji.kyoto.jp/news/2024/01/06/tourism-app', '産業・ビジネス', NOW() - INTERVAL '8 hours'),
  ('10', '北海道', '函館市', '高齢者見守りサービス開始', '函館市が高齢者見守りサービスを開始。IoTセンサーで安否確認。', '函館市は高齢者見守りサービス「はこだて見守りネット」を開始しました。自宅に設置したIoTセンサーで高齢者の日常行動をモニタリングし、異常を検知した際は家族や近隣住民に通知。安心して暮らせるまちづくりを支援しています。', 'https://www.city.hakodate.hokkaido.jp/news/2024/01/04/watching-service', '福祉・子育て', NOW() - INTERVAL '9 hours');